import Header from "./components/Header";
import Footer from "./components/Footer";
import React from "react";
import Items from "./components/Items";

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      items: [
        {
          id: 1,
          title: 'Cylinder Classic',
          img: 'cylinder.jpg',
          desc: 'null',
          category: 'cylinder hats',
          price: '50.49'
        },
        {
          id: 2,
          title: 'Fedora Classic',
          img: 'fordfedora.jpg',
          desc: 'null',
          category: 'fedoras',
          price: '50.49'
        },
        {
          id: 3,
          title: 'Cap Classic',
          img: 'cap.jpg',
          desc: 'null',
          category: 'caps',
          price: '50.49'
        },
        {
          id: 4,
          title: 'Winter Hat Classic',
          img: 'winter hat.jpg',
          desc: 'null',
          category: 'winter hats',
          price: '50.49'
        },
        {
          id: 5,
          title: 'Beret Classic',
          img: 'beret.jpg',
          desc: 'null',
          category: 'berets',
          price: '50.49'
        },
      ],
      orders: [
        {

        }
      ]
    }
    this.addToOrder = this.addToOrder.bind(this)
  }
  render () {
    return (
      <div className="wrapper">
        <Header />
        <Items items={this.state.items} onAdd={this.addToOrder}/>
        <Footer />
      </div>
    );
  }
  addToOrder(item) {
    this.setState({orders: [...this.state.orders, item]})
  }
}

export default App;
